(()=>{var r=()=>{document.addEventListener("DOMContentLoaded",()=>{let o=document.querySelector(".js-more"),a=document.querySelector("#app"),t=0;o&&a&&(fetch("https://jsonplaceholder.typicode.com/posts").then(s=>s.json()).then(s=>{console.log(s),s.forEach(e=>{if(e.id<=10){let c=document.createElement("article");c.innerHTML=`<h2 class="card__title">${e.title}</h2>
                                  <p class="card__body">${e.body}</p>`,a.append(c)}})}),o.addEventListener("click",()=>{t+=1,fetch("https://jsonplaceholder.typicode.com/posts").then(s=>s.json()).then(s=>{console.log(s),s.forEach(e=>{if(e.id>10*t&&e.id<=10*(t+1)){let c=document.createElement("article");c.innerHTML=`<h2 class="card__title">${e.title}</h2>
                                  <p class="card__body">${e.body}</p>`,a.append(c)}})})}));let d=document.querySelector("#users");d&&fetch("https://jsonplaceholder.typicode.com/users").then(s=>s.json()).then(s=>{console.log(s),s.forEach(e=>{if(e.id<=1){let c=document.createElement("article");c.innerHTML=`<h2 class="card__name">Name: ${e.name}</h2>
                                  <p class="card__username">Username: ${e.username}</p>
                                  <p class="card__email">Email: ${e.email}</p>
                                  <p class="card__address">Adress: ${e.address.city}, ${e.address.street} street, ${e.address.suite}</p>
                                  <p class="card__code">Zipcode: ${e.address.zipcode}</p>
                                  <p class="card__geo">Geo: ${e.address.geo.lat}, ${e.address.geo.lng}</p>
                                  <p class="card__phone">Phone: ${e.phone}</p>
                                  <p class="card__site">Website: ${e.website}</p>
                                  <p class="card__company">Company: ${e.company.name}</p>
                                  `,d.append(c)}})})})};r();})();
