(()=>{var n=()=>{document.addEventListener("DOMContentLoaded",()=>{let a=document.querySelector(".js-load"),d=document.querySelector(".js-more"),c=document.querySelector("#app");fetch("https://jsonplaceholder.typicode.com/posts").then(e=>e.json()).then(e=>{console.log(e),e.forEach(t=>{if(t.id<=10){let o=document.createElement("article");o.innerHTML=`<h2 class="card__title">${t.title}</h2>
                                  <p class="card__body">${t.body}</p>`,c.append(o)}})}),d.addEventListener("click",()=>{fetch("https://jsonplaceholder.typicode.com/posts").then(e=>e.json()).then(e=>{console.log(e);for(let t=0;t<10;t+=1){let o=document.createElement("article");o.innerHTML=`<h2 class="card__title">${e[t].title}</h2>
                                  <p class="card__body">${e[t].body}</p>`,c.append(o)}})})})};n();})();
